package com.typecasting;

public class Demo9 {

	public static void main(String[] args) {
		int n=257;
		int size=5;
		int [] a= new int[size];
		for(int i=0;i<a.length;n++)
		{
			if(n%2==0)
			{
			a[i]=n;
			i++;
			}
			
			
		}
		for(int i=0;i<a.length;i++)
		{
			System.out.println(a[i]);
		}
			
		// TODO Auto-generated method stub

	}

}
